function initContato(){console.log("✅ initContato iniciado!");let e=document.getElementById("formContato");if(!e){console.warn("⚠️ Formul\xe1rio de contato n\xe3o encontrado.");return}e.addEventListener("submit",t=>{t.preventDefault();let n=e.nome.value.trim(),o=e.email.value.trim(),a=e.mensagem.value.trim();if(n.length<3||!/\S+@\S+\.\S+/.test(o)||a.length<10){alert("Preencha os campos corretamente antes de enviar.");return}alert("Mensagem enviada com sucesso!"),e.reset()})}