function initLogin(){console.log("✅ initLogin iniciado!");let e=document.querySelector("#loginForm"),t=document.querySelector("#email"),i=document.querySelector("#senha"),o=document.querySelector("#loginMessage");if(!e){console.warn("⚠️ Formul\xe1rio de login n\xe3o encontrado.");return}e.addEventListener("submit",r=>{r.preventDefault(),o.textContent="",o.classList.remove("ativo");let n=t.value.trim(),a=i.value.trim();if(!/\S+@\S+\.\S+/.test(n)){alert("Digite um e-mail v\xe1lido."),t.focus();return}if(a.length<6){alert("A senha deve ter ao menos 6 caracteres."),i.focus();return}o.textContent="✅ Login realizado com sucesso!",o.classList.add("ativo"),e.reset(),setTimeout(()=>o.classList.remove("ativo"),3e3)})}